import React, { useState } from "react";
import Button from "../components/Button";
import API from "../utils/API";
import { FoodList, FoodListItem } from "../components/FoodList";
import { Container, Row, Col } from "../components/Grid";



function SearchGroupPage() {

  const [foodGroup, setFoodGroup] = useState([]);
  const [foodGroupSearch, setFoodGroupSearch] = useState("");

  const handleInputChange = event => {
    // Destructure the name and value properties off of event.target
    // Update the appropriate state
    const { value } = event.target;
    setFoodGroupSearch(value);
  };

  const handleFormSubmit = event => {
    // When the form is submitted, prevent its default behavior, get foods update the foods state
    event.preventDefault();
    API.getGroup(foodGroupSearch)
      .then(res => setFoodGroup(res.data))
      .catch(err => console.log(err));
  };

  function isCooked() {
        if (foodGroup.cooked === "yes") {
          return(
            <p>Is this food Cooked: Yes</p>
          )
        }
        else{
            return(
            <p>Is this food Cooked: No</p>
            )
        }


  }


  return (
    <div>

      <Container>
        <Row>
          <Col size="md-12">
            <form>
              <Container>
                <br />
                <Row>
                  <Col size="xs-9 sm-10">
                  <div className="form-group">
                    <select className="form-control" id="exampleFormControlSelect1" name="foodGroupSearch" value={foodGroupSearch} onChange={handleInputChange}>
                    <option>Grains/Carbs</option>
                    <option>Vegetables</option>
                    <option>Fruit</option>
                    <option>Calcium</option>
                    <option>Protein</option>
                    </select>
                  </div>
                  </Col>
                  <Col size="xs-3 sm-2">
                    <Button
                      onClick={handleFormSubmit}
                      type="success"
                      className="input-lg"
                    >
                        Search
                    </Button>
                  </Col>
                </Row>
              </Container>
            </form>
          </Col>
        </Row>
        <Row>
          <div class="col-xs-12 resultscol">
            {!foodGroup.length ? (
              <div></div>
            ) : (
              <FoodList>
                {foodGroup.map(food => {
                  return (
                    <FoodListItem
                      key={food.name}
                      name={food.name}
                      foodGroup={food.foodGroup}
                      foodGroup={food.foodGroup}
                      cooked={isCooked()}
                      ingredients={food.ingredients}
                    />
                  );
                })}
              </FoodList>
            )}
          </div>
        </Row>
      </Container>
    </div>
  );
}

export default SearchGroupPage;
